import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { setPlace } from '../redux/actions';
import { Autocomplete } from '@mui/material';
import { TextField } from '@mui/material';

const AutocompleteInput = () => {
  const dispatch = useDispatch();
  const [inputValue, setInputValue] = useState('');

  const handlePlaceSelect = (place) => {
    dispatch(setPlace(place));
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const loadPlaces = (inputValue) => {
    const autocompleteService = new window.google.maps.places.AutocompleteService();
    autocompleteService.getPlacePredictions(
      { input: inputValue },
      (predictions, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
          const places = predictions.map((prediction) => ({
            description: prediction.description,
            place_id: prediction.place_id,
          }));
          handlePlaceSelect(places);
        }
      }
    );
  };

  return (
    <Autocomplete
      options={[]}
      autoHighlight
      inputValue={inputValue}
      onInputChange={handleInputChange}
      onChange={(event, newValue) => {
        setInputValue(newValue ? newValue.description : '');
        handlePlaceSelect(newValue);
      }}
      onOpen={() => {
        if (inputValue) {
          loadPlaces(inputValue);
        }
      }}
      renderInput={(params) => (
        <TextField
          {...params}
          label="Search for a place"
          variant="outlined"
          fullWidth
        />
      )}
    />
  );
};

export default AutocompleteInput;
